import sailpoint.object.Identity;

System.out.println("Firstname Field Identity Name Checking : "+name);
if(null != name && void != name){
Identity  idenObj = context.getObjectByName(Identity.class,name);
System.out.println("Firstname Of The Identity  : "+idenObj.getFirstname());
return idenObj.getFirstname();
}
return null;

